package faker.specs.phoneimport spock.lang.Specificationimport faker.Phone;import faker.Stringsimport faker.specs.support.Localized;
@Localized("fr_FR")class PhoneNumberTest extends Specification {

    def "number"(def number) {        expect: "the generated number is not empty"		assert number		where: "the phone number is generated by Phone.number()"        number << (1..1000).collect { Phone.number() }    }		def "mobile"(def mobile) {		expect: "the generated mobile number is not empty"		assert mobile		where: "the mobile number is generated by Phone.mobile()"		mobile << (1..1000).collect { Phone.mobile() }	}}